import styled from "styled-components";
import React from 'react';
import './index.css';


const Input = styled.div`
    //background-color: red;
    display: flex;
    border-radius: 4px;
    padding:0.5em;
    padding-left:0;
    vertical-align:middle;
    `;

const Index = styled.input`
    background-color: transparent;
    display: flex;
    text-align: left;
    height:100%;
    transition: 0.25s ease-in-out;
    cursor: pointer;
    border:none;
    border-radius: 4px;
    font-family: "Courier New";
    &:focus {
        outline: none;
    }
    `;

const Tags = ({childInputRef}) => {

    const [tags, setTags] = React.useState([]);
    const [error, setError] = React.useState("");

    const handleTags =  event => {
        if (event.key === " " && event.target.value !== "") {
            setTags([...tags, event.target.value]);
            event.target.value = "";
        }
        else if (event.key === "Backspace" && event.target.value == 0){
            const tagsCopy = [...tags];
            tagsCopy.pop();
            event.preventDefault();
            setTags(tagsCopy);
        }
        else if(tags.length < 1 && event.key === "Backspace"){
            setError("Since there is no tags you can't able to delete any tags");
        }
        else if(event.target.value === "" && event.key === "Enter"){
            setError("The tag should be one character long!");
        }
    };

    const removeTags = index => {
        setTags([...tags.filter(tag => tags.indexOf(tag) !== index)]);
        };


    return <Index
            type="text"
            onKeyDown={event => handleTags(event)}
            placeholder="type here..."
            ref={childInputRef}
        >    {tags.map((tag, index) => (
            <div key={index}>
                <span className="single-tag" onDoubleClick={() => removeTags(index)}>{tag}</span>
            </div>
        ))}</Index>

}
export default Tags;
