import styled from 'styled-components';

const Tag = styled.span.attrs(props => ({
    // or we can define dynamic ones
    $value: props.$value || 1,
    $karvai: props.$karvai || true
  }))`
    //display:block;
    padding:0.5em;
    position:sticky;
    float:left;
    vertical-align:middle;
    background-color: ${props => props.$karvai?"#e4e4e4" : "#3C343110"};
    color: #787878;
    border-radius: 10px;
    margin-right:0.5em;
    text-align:center;
    grid-column-end: span ${props => props.$value};
    `
const Index = (props) => {
    return <Tag $value={props.value} $karvai={false}>{props.children}</Tag>; 
};
export default Index;
