import React, { useCallback } from "react";
import styled from "styled-components";
import './index.css';
import data from '../../../Data/notesData.json'

// {"type":"karvai", "sollu": "dhee-ee-ngu", "value":"tha", "articulation": 2, "speed": 2}


const NoteGrid = styled.table`
    border:none;
    border-collapse: collapse;
    table-layout:fixed;
    width:100%;
    margin-top:0.5em;
    margin-bottom:1em;
    overflow-wrap: break-word;
    word-wrap: break-word;
    hyphens: auto;
    flex-direction:row;
    flex-shrink:1;
`;

const NoteTag = styled.span.attrs(props => ({
    $karvai: props.$karvai,
    $rowIndex: props.$rowIndex,
    $tagIndex: props.$tagIndex
}))`
    padding: 0.5em 0.75em;
    background-color: ${props => props.$karvai === true? "#787878" : "#3C343110"} ;
    border-radius: 7px;
    margin-right:0.5em;
    white-space:nowrap;
    min-width:100px;
    text-align:left;
    -webkit-appearance: none;
    &:focus {
        outline: none;
    }
`;

