import styled from "styled-components";
import React, { useState } from 'react';
import { TagsInput } from "react-tag-input-component";

const Input = styled.textarea`
    background-color: #3C343109;
    display: flex;
    flex-flow: column wrap;
    text-align: left;
    transition: 0.25s ease-in-out;
    box-sizing: border-box;
    cursor: pointer;
    padding: 0.75em;
    padding-top: 0.5em;
    margin: 0.75em 0;
    border:none;
    border-radius: 4px;
    font-family: "Courier New";
    font-size: 0.9em;
    overflow: hidden;
    resize: none;
    `;

    const [tags, setTags] = React.useState([]);
    const [error, setError] = React.useState("");

    const handleTags =  event => {
        if (event.key === "Enter" && event.target.value !== "") {
            setTags([...tags, event.target.value]);
            event.target.value = "";
        }
        else if (event.key === "Backspace" && tags.length && event.target.value == 0){
            const tagsCopy = [...tags];
            tagsCopy.pop();
            event.preventDefault();
            setTags(tagsCopy);
        }
        else if(tags.length < 1 && event.key === "Backspace"){
            setError("Since there is no tags you can't able to delete any tags");
        }
        else if(event.target.value == "" && event.key === "Enter"){
            setError("The tag should be one character long!");
        }

        return <div className="tags">
        {tags.map((tag, index) => (
            <div className="single-tag" key={index}>
                <span>{tag}</span>
                <i> <MdClose /> </i>
            </div>
        ))}

        <input
            type="text"
            onKeyDown={event => handleTags(event)}
            placeholder="Write some tag and press enter"
        />
    </div>
    };
