import styled from "styled-components";
import React, { useState } from 'react';

const Input = styled.textarea`
    background-color: #3C343108;
    display: flex;
    flex-flow: column wrap;
    text-align: left;
    transition: 0.25s ease-in-out;
    box-sizing: border-box;
    cursor: pointer;
    padding: 0.75em;
    padding-top: 0.5em;
    margin: 0.75em 0;
    border:none;
    resize:none;
    border-radius: 4px;
    font-family: "Courier New";
    font-size: 0.9em;
    overflow:hidden;
    `;

const Index = (props) => {
    return <Input rows="1" />; 
};
export default Index;
