import React from "react";
import styled from "styled-components";

const NoteGrid = styled.table`
    border:none;
    border-collapse: collapse;
    table-layout:fixed;
    width:100%;
    margin-top:0.5em;
    margin-bottom:1em;
    overflow-wrap: break-word;
    word-wrap: break-word;
    hyphens: auto;
`;

const NoteTag = styled.span.attrs(props => ({

}))`
    padding:0.3em;
    background-color: #3C343110;
    border-radius: 7px;
    margin-right:0.35em;
    white-space:nowrap;
    min-width:100px;
    text-align:left;
`;

const Button = styled.button.attrs( props => ({

})
)`
    background-color: transparent;
    font-family:'Courier New', Courier, monospace;
    padding:0.3em;
    padding-left:0.55em;
    padding-right:0.55em;
    border:0.7px solid #3c3431;
    align-self:center;
    margin:0.5em;
    margin-bottom:0.85em;
    margin-top:0;
    &:hover{
        background-color: #3c343112;
    }

`

const Index = (props) => {

    const parse = require('html-react-parser');

    const [grid, setGrid] = React.useState({
        rows: [
            {
                tags: [
                    {value: ""}
                ]
            }
        ]
    });

    function addTag (value) {
        var gridCopy = {...grid};
        gridCopy.rows[gridCopy.rows].tags[gridCopy.rows[gridCopy.rows.length-1].tags.length-1] = value;
        gridCopy.rows[gridCopy.rows].tags.push({value:""});
        setGrid(gridCopy);
    }

    function backspaceTag () {
        var gridCopy = {...grid};
        gridCopy.rows[gridCopy.rows.length-1].tags.pop();
        setGrid(gridCopy);
    }

    function newLine () {
        var gridCopy = {...grid};
        gridCopy.rows.push({tags: {value:""}});
        setGrid(gridCopy)
    }

    function handleTags (event) {
        var contenteditable = document.querySelector('[contenteditable]'),
        text = contenteditable.textContent;

        if (event.key === " " && text !== "") {
            addTag(text);
        }
        else if (event.key === "Backspace" && text===""){
            backspaceTag();
        }
        else if (event.key === "Enter") {
            newLine();
        }

    }

    function renderRows () {
        return grid.rows.map((row) => <tr>{row.tags.map((tag) => <td><NoteTag>{parse(tag.value)}</NoteTag></td>)}</tr>)
    }


    return <div style={{height:'100%', whiteSpace:'nowrap', position: 'relative'}}>
        <NoteGrid>{renderRows()}</NoteGrid>
        <ul>
            <Button>new nadai</Button>
            <Button>new korvai</Button>
            <Button>new theermanam</Button>
            <Button>new morah</Button>
        </ul>
    </div>
};
export default Index;
